{"filter":false,"title":"plusOne.py","tooltip":"/leet/array/plusOne.py","undoManager":{"mark":4,"position":4,"stack":[[{"start":{"row":0,"column":0},"end":{"row":3,"column":0},"action":"remove","lines":["\"\"\"","Your module description","\"\"\"",""],"id":1},{"start":{"row":0,"column":0},"end":{"row":24,"column":0},"action":"insert","lines":["","class Solution:","    def plusOne(self, digits):","        n = len(digits)","","        # move along the input array starting from the end","        for i in range(n):","            idx = n - 1 - i","            # set all the nines at the end of array to zeros","            if digits[idx] == 9:","                digits[idx] = 0","            # here we have the rightmost not-nine","            else:","                # increase this rightmost not-nine by 1","                digits[idx] += 1","                # and the job is done","                return digits","","        # we're here because all the digits are nines","        return [1] + digits","","s=Solution()","digits = [1,9]","print(s.plusOne(digits))",""]}],[{"start":{"row":0,"column":0},"end":{"row":0,"column":1},"action":"insert","lines":["#"],"id":2}],[{"start":{"row":0,"column":1},"end":{"row":0,"column":2},"action":"insert","lines":[" "],"id":3}],[{"start":{"row":0,"column":2},"end":{"row":0,"column":53},"action":"insert","lines":["https://leetcode.com/problems/plus-one/submissions/"],"id":4}],[{"start":{"row":0,"column":53},"end":{"row":1,"column":0},"action":"insert","lines":["",""],"id":5}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":1,"column":0},"end":{"row":1,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1609033106656,"hash":"c64205ab45e5c1510b0c55b38677b1e6394ec6c1"}